mapper.select:
select

from job

  1 = 1

    AND user_id = #{userId}


    AND school_id = #{schoolId}


    AND address = #{address}


    AND `name` LIKE CONCAT(CONCAT('%', #{keyword}), '%')


    AND education = #{education}


    AND status = #{status}


order by id desc

